import{a as p,b as m}from"./chunk-BDTI3S4C.js";import{a as g}from"./chunk-52IIKTQU.js";import{Db as d,Eb as i,Ma as o,Ya as n,ba as u,fb as t,gb as e,hb as r,yb as l}from"./chunk-K4XZND3X.js";var h=()=>({src:"assets/img/storia/storia_1.jpg",height:"1200",width:"1600"}),f=()=>({src:"assets/img/storia/storia_2.jpg",height:"720",width:"960"}),b=()=>({src:"assets/img/storia/storia_3.jpg",height:"1367",width:"1367"}),C=()=>({src:"assets/img/storia/storia_4.jpg",height:"1756",width:"1404"}),I=()=>({src:"assets/img/storia/storia_5.jpg",height:"633",width:"843"}),z=()=>({src:"assets/img/storia/storia_6.jpg",height:"720",width:"960"}),F=()=>({src:"assets/img/storia/storia_7.jpg",height:"1600",width:"1199"}),L=()=>({src:"assets/img/storia/storia_8.jpg",height:"960",width:"960"}),E=()=>({src:"assets/img/storia/storia_9.jpg",height:"1618",width:"1493"}),w=()=>({src:"assets/img/storia/storia_10.jpg",height:"1024",width:"1024"}),M=(()=>{let a=class a{ngOnInit(){this.lightbox=g("storia-gallery"),this.lightbox.init()}ngAfterViewInit(){m()}ngOnDestroy(){this.lightbox.destroy()}};a.\u0275fac=function(s){return new(s||a)},a.\u0275cmp=u({type:a,selectors:[["w-storia"]],standalone:!0,features:[d],decls:17,vars:21,consts:[["id","storia",1,"page-section"],["id","storia-gallery",1,"container"],[3,"firstImage","secondImage"],["data-aos","fade-up","data-aos-duration","1000"],["normalizeHeightClass","sm",3,"firstImage","secondImage"],["normalizeHeightClass","sm",3,"firstImage","secondImage","isLast"]],template:function(s,S){s&1&&(t(0,"section",0)(1,"div",1)(2,"p"),l(3,` Lugnano \xE8 un piccolo, ma \u201Cgrande e prezioso" centro della periferia di Rieti, che vanta un'indimenticabile tradizione storica ed una ricchezza artistica tra le pi\xF9 importanti della Sabina. Questo paese \xE8 una delle ultime testimonianze degli antichi castelli e feudi sabinesi. Le sue origini vanno fatte risalire al VI secolo d.C., ma ebbe il massimo splendore nel basso Medioevo. Costruito su roccia viva di un colle aereo alle pendici di Terminillo, Lugnano \xE8 stato fondato dalle trib\xF9 formate dai superstiti dell'antica citt\xE0 di Vazia. Il primo nome fu Lucinae Fanum, dal tempio sacro alla dea Juno Lucina che sorgeva su quel monte; in secondo tempo fu chiamato Unianus, per l'aggregazione dei profughi dell'antica Vazia che si erano riuniti per formare il nuovo centro abitato (cos\xEC si legge nello stemma, che si trova impresso nell'antica Chiesa Badiale di S. Croce). `),e(),r(4,"w-image-row",2),t(5,"p",3),l(6," Al tempo di Arrigo III (1048) il Castello di Lugnano fu ammesso all'Abbazia di Farfa. Nel 1252 Lugnano, insieme ai vicini Lisciano, S.Rufina e Cantalice, \xE8 annesso al regno di Napoli. Lugnano, unitamente a Lisciano, Categne, Petescia e Cantalice, collabor\xF2 per costruire nel 1309 il secondo quartiere, quello di S. Croce della nuova citt\xE0 angioina di Cittaducale, fondato da Carlo d\u2019Angi\xF2. Nelle sue intenzioni, il nuovo centro doveva diventare capoluogo della provincia circostante. A Cittaducale vennero fatte confluire quindi le famiglie pi\xF9 importanti del territorio sabino ed una di queste famiglie, quella dei Quirini, proveniva da Lugnano. I nobili Quirini abitarono a Cittaducale per una cinquantina d\u2019anni, finch\xE8, nel 1363, un\u2019epidemia di peste non li costrinse a ritirarsi nuovamente nella nat\xECa Lugnano, ed \xE8 probabile che siano stati proprio loro a portare nel paese la deliziosa statuetta della Madonnina del Fiore. Intanto nella parte alta di Lugnano fu costruito il Convento di S. Rocco, meta tutt\u2019ora di tantissimi escursionisti e gite scolastiche: dal convento si possono ammirare i bellissimi paesi Lugnano e Coccodrillo, suo vicino e amico da sempre, e la vasta piana reatina. Sul Colle San Rocco, in posizione dominante il paese e la vallata, rimangono i resti del convento indicato con lo stesso nome. Il convento \xE8 ormai distrutto e rimangono solo alcuni poderosi archi a fare da quinta scenografica alla montagna, visibili anche da molto lontano. Poco pi\xF9 in alto rimangono in piedi le mura di cinta della chiesa. "),e(),r(7,"w-image-row",4),t(8,"p",3),l(9," Nel 1630 Lugnano si distacc\xF2 dall\u2019unione con Cittaducale; per la sua posizione controllava la montagna con i suoi grandi pascoli che, dati in affitto, costituivano una delle principali entrate della comunit\xE0; grandi greggi di pecore al pascolo, per migliaia di capi, assieme a mandrie di bovini e cavalli, costituivano un ingente patrimonio che la presenza del confine statale permetteva di moltiplicare e monetizzare grazie alla possibilit\xE0 di esportare nello Stato pontificio i prodotti dell\u2019allevamento. Questa fioritura economica sembra per\xF2 arrestarsi nel Seicento, anche per le condizioni generali della penisola italiana, aggravate dall\u2019esasperato fiscalismo dei Vicer\xE8 spagnoli e dalla ripresa in grande stile del banditismo, che s\u2019appoggiava ai potentati politici dell\u2019epoca. Nel 1703 la crisi venne aggravata ulteriormente dal terremoto per poi continuare nel 1799 col ritorno della guerra. Il Regno si trasform\xF2 in Repubblica Partenopea, le truppe della Francia rivoluzionaria cominciarono ad occupare Rieti. Tutta la montagna inizi\xF2 a ribellarsi al nuovo regime fino a quando, in seguito ad una rappresaglia contro i paesi sul confine, Lugnano riusc\xEC a resistere al saccheggio e al fuoco. "),e(),r(10,"w-image-row",2),t(11,"p",3),l(12,` Nel 1928 il vecchio comune di Lugnano di Villa Troiana venne assorbito da quello di Rieti, perdendo l\u2019autogoverno. Solo trent\u2019anni dopo, nel 1958, con la costruzione dell\u2019Amministrazione Separata dei Beni Civici di Vazia, il territorio riusc\xEC a recuperare l\u2019autonomia riguardo ai beni e agli usi civici. La prova che Lugnano abbia raggiunto la massima espansione nel 1300 la si pu\xF2 trovare visitando i Musei Vaticani: nella galleria dove sono dipinte le antiche regioni d' Italia, osservando la regione Sabina, si possono ammirare accanto alle torri e alle mura di Rieti e Cittaducale, anche quelle di Lugnano. Si dice che proprio in questo periodo Carlo D'Angi\xF2, di ritorno dalla Francia, visitando il feudo di Lugnano regal\xF2 alla popolazione la statuina della "Madonna del Fiore": si tratta di scultura in avorio ricavata da una zanna di elefante, raffigurante la Vergine con il Bambino ed un fiore nella mano: \xE8 opera di uno scultore anonimo della scuola francese del XIII secolo. Ne esistono solamente tre esemplari al mondo: uno si trova a Parigi, uno a Pisa e uno a Lugnano. La scultura \xE8 conservata nella Chiesa di S. Maria in Categne, amore e orgoglio degli abitanti di Lugnano e Coccodrillo. `),e(),r(13,"w-image-row",4),t(14,"p",3),l(15," A Lugnano, al tempo del suo splendore, sorgevano quattro chiese: S. Croce, S. Maria, S. Maria della Misericordia (detta dal volgo di Categne) e quella di S. Giovanni Battista. Di recente \xE8 stato rivalutato il Santuario della Madonna, al quale \xE8 stato intitolato un Concorso Artistico-Letterario che, nell\u2019intenzione degli organizzatori, dovrebbe favorire un incremento della devozione popolare ed uno sviluppo turistico del paese. "),e(),r(16,"w-image-row",5),e()()),s&2&&(o(4),n("firstImage",i(11,h))("secondImage",i(12,f)),o(3),n("firstImage",i(13,b))("secondImage",i(14,C)),o(3),n("firstImage",i(15,I))("secondImage",i(16,z)),o(3),n("firstImage",i(17,F))("secondImage",i(18,L)),o(3),n("firstImage",i(19,E))("secondImage",i(20,w))("isLast",!0))},dependencies:[p]});let c=a;return c})();export{M as StoriaComponent};
